package djh.learn.dataTypes;

import java.util.Date;

public class DefaultValues {
	static byte bt;
	static short st;
	static int num;
	static long ln;
	static float ft;
	static double db;
	static char ch;
	static boolean bl;
	static Byte bte;
	static Integer integer;
	static String str;
	static Date dt;

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		System.out.println("Default value for byte is "+bt);
		System.out.println("Default value for short is "+st);
		System.out.println("Default value for int is "+num);
		System.out.println("Default value for long is "+ln);
		System.out.println("Default value for float is "+ft);
		System.out.println("Default value for double is "+db);
		System.out.println("Default value for char is "+ch);
		System.out.println("Default value for boolean is "+bl);
		System.out.println("Default value for Byte is "+bte);
		System.out.println("Default value for Integer is "+integer);
		System.out.println("Default value for String is "+str);
		System.out.println("Default value for Date is "+dt);
	}

}
