package djh.learn.dataTypes;

public class Primitives {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
			byte bt  = 125;
			System.out.println("Byte data type is "+bt);
			short st = 345;
			System.out.println("Short data type is "+st);
			int num = 567;
			System.out.println("Integer data type is "+num);
			long ln = 67886688;
			System.out.println("Long data type is "+ln);
			float ft = 45.55f;
			System.out.println("Float data type is "+ft);
			double db = 64545.454544555d;
			System.out.println("Double data type is "+db);
			char ch = 'D';
			System.out.println("Character data type is "+ch);
			boolean bl = true;
			System.out.println("Boolean data type is "+bl);
			
	}

}
